templates.template:
/*
 * @name: ${name}_E
 * @description:${description}
 */
typedef enum {
	State_${TaskName}_Stop = 99,
	State_${TaskName}_Init = 0,
	State_${TaskName}_Step1,
	State_${TaskName}_Finished,
}${name}_E;

uint16_t Task_${TaskName}(STATE_MACHINE_S * sm)
{
	if(1 != sm->Enable){
		return (sm->Finished = 0);
	}

	switch (sm->StatePresent) {
		case State_${TaskName}_Start:
			sm->Finished = 0;
			sm->Timmer = 0;

			/* State Transition */
			sm->StatePresent = State_${TaskName}_Step1;
			break;
		case State_${TaskName}_Step1:

			/* State Transition */
			sm->StatePresent = State_${TaskName}_Step2;
			break;
		case State_${TaskName}_Finished:
			sm->Finished = 1;

			/* State Transition */
			sm->StatePresent = State_${TaskName}_Start;
			break;
		default:

			break;
	}

	return (sm->Finished);
}

/*
 * @name: STATE_MACHINE_S
 * @description: ${description}
 */
typedef struct {
	/* Control */
	uint16_t Enable;
	
	/* Setting */

	/* Variable */
	uint16_t StatePresent;
	uint16_t TimmerEnable;
	uint32_t Timmer;

	/* Output */
	uint16_t Finished;
}STATE_MACHINE_S;

/*
 * @name: ${name}_S
 * @description: ${description}
 */
typedef struct {
	/* Control */
	uint16_t Start;
	uint16_t Stop;
	
	/* Input */
	
	/* Setting */
	
	/* Variable */
	uint16_t Running;
	
	/* State */
	uint16_t Finished;
	
	/* Output */
	
}${name}_S;
